import UIKit

protocol StockListDisplayLogic: class {
    func displaySomething(viewModel: StockList.Something.ViewModel)
}

class StockListViewController: UIViewController, StockListDisplayLogic {
    var interactor: StockListBusinessLogic?
    var router: (NSObjectProtocol & StockListRoutingLogic & StockListDataPassing)?

    var stockListTableView: UITableView!

    // MARK: Object lifecycle

    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }

    required init?(coder: NSCoder) {
        fatalError("init(coder:) has not been implemented")
    }

    // MARK: Setup

    private func setup() {
        let viewController = self
        let interactor = StockListInteractor()
        let presenter = StockListPresenter()
        let router = StockListRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }

    // MARK: Routing

    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }

    // MARK: View lifecycle

    override func viewDidLoad() {
        super.viewDidLoad()
        setupTableView()
        doSomething()
    }

    func setupTableView() {
        stockListTableView = UITableView(frame: .zero, style: .grouped)
        stockListTableView.delegate = self
        stockListTableView.dataSource = self
        stockListTableView.translatesAutoresizingMaskIntoConstraints = false
        view.addSubview(stockListTableView)
        stockListTableView.topAnchor.constraint(equalTo: self.view.topAnchor).isActive = true
        stockListTableView.bottomAnchor.constraint(equalTo: self.view.bottomAnchor).isActive = true
        stockListTableView.leadingAnchor.constraint(equalTo: self.view.leadingAnchor, constant: 16).isActive = true
        stockListTableView.trailingAnchor.constraint(equalTo: self.view.trailingAnchor, constant: -16).isActive = true
        stockListTableView.showsVerticalScrollIndicator = false
        stockListTableView.register(StockCell.self, forCellReuseIdentifier: StockCell.reuseIdentifier)
        stockListTableView.backgroundColor = .clear
        stockListTableView.separatorColor = .clear
    }
    // MARK: Do something

    func doSomething() {
        let request = StockList.Something.Request()
        interactor?.doSomething(request: request)
    }

    func displaySomething(viewModel: StockList.Something.ViewModel) {
        // nameTextField.text = viewModel.name
    }
}

