    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        guard let cell = tableView.dequeueReusableCell(withIdentifier: StockCell.reuseIdentifier, for: indexPath) as? StockCell else {
            fatalError()
        }
        cell.configure(with: Stock(ticker: "AAPL", name: "Apple", currentPrice: 120.31, priceChange: -3.13, isFaved: true))
        return cell
    }

